mapper.select:
SELECT b.board_no, b.board_category_no, b.board_title, b.board_writer, b.board_content, b.board_view, b.board_write_date, b.board_update_date, bc.board_category_name FROM board b JOIN board_category bc ON b.board_category_no = bc.board_category_no ORDER BY b.board_No DESC LIMIT #{startPost} , #{viewPost}
ORDER BY b.board_No DESC LIMIT 1 , #{viewPost}
ORDER BY b.board_No DESC LIMIT #{startPost} , #{viewPost}

SELECT board_password
FROM board
WHERE board_no=#{boardNo}

SELECT * FROM board_reply
WHERE board_no = #{boardNo}
ORDER BY board_reply_no DESC LIMIT 5
